import{f as l,D as t,c as i,o as r,aq as s,H as o}from"./chunks/framework.D90EkY4s.js";const h=JSON.parse('{"title":"关键指标","description":"灾后恢复关键指标文档","frontmatter":{"title":"关键指标","editLink":true,"description":"灾后恢复关键指标文档","layout":"doc","head":[["meta",{"name":"keywords","content":"Linux, README, Introduce"}]],"outline":"deep","prev":{"text":null,"link":null},"next":{"text":null,"link":null}},"headers":[],"relativePath":"posts/6749a8d7.md","filePath":"docs/recovery/key-point.md","lastUpdated":1748252090000}'),c={name:"posts/6749a8d7.md"};function d(p,e,u,m,_,b){const a=t("NolebaseGitContributors"),n=t("NolebaseGitChangelog");return r(),i("div",null,[e[0]||(e[0]=s('<h1 id="关键指标" tabindex="-1">关键指标 <a class="header-anchor" href="#关键指标" aria-label="Permalink to &quot;关键指标&quot;">​</a></h1><p>在灾难恢复方面，目前业界公认的有三个目标值得努力：</p><ul><li>恢复时间：即企业能够忍受多长实际没有 IT，处于停业状态</li><li>网络恢复：网络多长时间能够恢复</li><li>业务恢复：业务层面的恢复</li></ul><p>在整个恢复过程中，有两个关键的衡量指标：</p><ul><li>RTO：Recovery Time Objective，指的是在灾难发生后，从 IT 系统宕机导致的业务停顿开始，到 IT 系统恢复至可以支持各部门运作、恢复运营时，这两者之间的时间段称为 RTO</li><li>RPO：Recovery Point Objective，指的是对于系统和应用数据而言，要实现能够恢复至各个部门间的业务运作，系统以及生产数据应该恢复到怎么样的更新程度。这种更新程度可以是上一周的备份数据，也可以是上一次交易的实时数据。</li></ul>',5)),o(a),o(n)])}const k=l(c,[["render",d]]);export{h as __pageData,k as default};
