import{_ as s,C as a,c as o,o as r,aq as c,G as t}from"./chunks/framework.pQnWedaC.js";const m=JSON.parse('{"title":"代码仓库搭建","description":"私有代码仓库搭建","frontmatter":{"title":"代码仓库搭建","editLink":true,"description":"私有代码仓库搭建","layout":"doc","head":[["meta",{"name":"keywords","content":"Linux, Git, GitLab"}]],"outline":"deep","prev":{"text":"业务上线检查事项列表","link":"posts/55e3861d"},"next":{"text":null,"link":null}},"headers":[],"relativePath":"posts/894a942b.md","filePath":"docs/infrastructure/gitlab.md","lastUpdated":1744200528000}'),n={name:"posts/894a942b.md"};function b(d,e,p,u,h,k){const l=a("NolebaseGitContributors"),i=a("NolebaseGitChangelog");return r(),o("div",null,[e[0]||(e[0]=c('<h1 id="代码仓库搭建" tabindex="-1">代码仓库搭建 <a class="header-anchor" href="#代码仓库搭建" aria-label="Permalink to &quot;代码仓库搭建&quot;">​</a></h1><p>出于业务以及维护的需求考虑，使用成熟的开源软件 GitLab 作为内部代码仓库。对于轻量使用的情况下，可以选择轻量级的 Gogs、Gitea 等作为内部代码仓库使用。这里我们不做介绍。</p><p>对于 GitLab 的搭建，有以下两种方式进行运行，分别为：</p><ul><li>物理机（虚拟机）运行</li><li>容器化运行</li></ul><p>对于不同的运行方式，安装部署的方式也不尽相同，这里分别进行介绍。</p><h2 id="物理机-虚拟机-运行" tabindex="-1">物理机（虚拟机）运行 <a class="header-anchor" href="#物理机-虚拟机-运行" aria-label="Permalink to &quot;物理机（虚拟机）运行&quot;">​</a></h2><p>物理机（虚拟机）安装运行，主要的注意事项如下：</p><ul class="task-list"><li class="task-list-item"><input type="checkbox" id="cbx_0" disabled="true"><label for="cbx_0"> 确认支持的版本</label></li><li class="task-list-item"><input type="checkbox" id="cbx_1" disabled="true"><label for="cbx_1"> 修改存储目录</label></li><li class="task-list-item"><input type="checkbox" id="cbx_2" disabled="true"><label for="cbx_2"> 配置反向代理</label></li><li class="task-list-item"><input type="checkbox" id="cbx_3" disabled="true"><label for="cbx_3"> 配置高可用</label></li><li class="task-list-item"><input type="checkbox" id="cbx_4" disabled="true"><label for="cbx_4"> 配置备份</label></li><li class="task-list-item"><input type="checkbox" id="cbx_5" disabled="true"><label for="cbx_5"> 配置邮件通知</label></li></ul><h3 id="下载安装" tabindex="-1">下载安装 <a class="header-anchor" href="#下载安装" aria-label="Permalink to &quot;下载安装&quot;">​</a></h3><div class="important custom-block github-alert"><p class="custom-block-title">注意事项：</p><p></p><p>如果没有必要的数据安全等方面的需求，请尽量下载国外的 GitLab 版本，而不是国内的极狐版本。</p></div><h2 id="容器化运行" tabindex="-1">容器化运行 <a class="header-anchor" href="#容器化运行" aria-label="Permalink to &quot;容器化运行&quot;">​</a></h2><h3 id="使用-docker-运行" tabindex="-1">使用 Docker 运行 <a class="header-anchor" href="#使用-docker-运行" aria-label="Permalink to &quot;使用 Docker 运行&quot;">​</a></h3><h3 id="使用-k8s-运行" tabindex="-1">使用 K8s 运行 <a class="header-anchor" href="#使用-k8s-运行" aria-label="Permalink to &quot;使用 K8s 运行&quot;">​</a></h3>',13)),t(l),t(i)])}const _=s(n,[["render",b]]);export{m as __pageData,_ as default};
